INSERT INTO `businessman` (`id`, `cid`, `name`, `img_dir`, `des`, `img`, `post`, `sex`, `age`, `education`, `school_type`, `school`, `school_type2`, `school2`, `has_professional_certificate`, `professional_certificate`, `work_experience`, `enterprise_type`, `enterprise_field`, `has_management_work`, `other`, `create_time`, `update_time`, `is_delete`)
VALUES
	(1,1,'唐宁','static/files/busimg/d32cdd4996164c9dbbb588dcb6a6247c.png','董事长/创始人\r\n\r\n唐宁先生是宜人贷也是宜信的创始人，自宜信公司创立以来一直担任公司董事长以及首席执行官一职，并兼任宜人贷董事长。\r\n\r\n2014年12月，唐宁当选北京市网贷行业协会会长，该协会由包括宜信在内的约30家行业内企业共同创立，是中国第一家正式注册并受监管机构监管的网贷行业协会。 2011年7月，唐宁荣获年度全球微型金融领军人物提名奖。2012年6月，唐宁被北京市委评为“北京市创先争优优秀共产党员”。\r\n\r\n唐宁还担任工业和信息化部中小企业政策咨询团专家，中国小额信贷服务中介机构联席会理事长等职务。\r\n\r\n唐宁曾就读于北京大学数学系，后赴美学习经济学，学成后在华尔街DLJ投资银行从事金融、电信、媒体及高科技类企业的上市、发债和并购业务。2000年回国后加入知名的电信软件与服务提供商亚信集团，任战略投资和兼并收购总监。\r\n','5.jpg.png','董事长',1,45,2,3,'美国南方大学',2,'北京大学',0,NULL,5,2,2,2,'年度全球微型金融领军人物提名奖“北京市创先争优优秀共产党员”','2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(2,1,'方以涵','static/files/busimg/5b0cfb2246eb475587e20b1f029e297b.png','总经理\r\n\r\n方以涵女士于2011年加入宜信，有15年在互联网、大数据和金融服务的工作经验。\r\n\r\n加入宜人贷之前，方以涵曾担任美国上市公司IAC/Ask.com 副总裁一职，负责全球搜索与问答相关的战略、产品和运营工作。\r\n\r\n方以涵获美国哥伦比亚大学天文系和电子工程系双硕士学位，本科就读于中国科学技术大学少年班。','6.jpg.png','总经理',0,NULL,2,3,'哥伦比亚大学',2,'中国科学技术大学',0,NULL,4,2,3,2,'2015中国互联网金融领军榜·十大女领军人物奖”“品牌中国“2015中国互联网金融品牌新领军女性奖”“互联网金融先锋人物奖”','2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(3,1,'丛郁','static/files/busimg/482e56ff2df24d79ac3ded763bede822.png','财务主管\r\n\r\n丛郁先生在高科技公司企业融资、战略市场合作, 股权投资领域有丰富经验。\r\n\r\n加入宜人贷之前，丛郁担任德意志银行北京代表处首席代表，并担任德意志银行中国区高科技(TMT)投行部负责人和董事。丛郁曾在美国投资银行Needham & Co.任职，并在Piper Jaffray& Co.从事与科技行业相关的股票证券分析工作。\r\n\r\n丛郁取得美国加州大学伯克利分校哈斯商学院MBA学位 、美国伊利诺伊大学香槟分校博士学位、中国科学技术大学学士学位。\r\n','7.jpg.png','财务主管',1,NULL,3,3,'美国加州大学伯克利分校哈斯商学院',3,'美国伊利诺伊大学香槟分校',0,NULL,0,2,2,2,NULL,'2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(4,1,'潘亦婷','static/files/busimg/fa4d2755a7a84dfd9795f51d865d7821.png','风控主管\r\n\r\n潘亦婷女士在美国金融机构风险管理部门任职多年，在风险分析和控制领域积累了丰富的经验，曾任美国知名金融企业Capital One董事，领导及负责经济资本的分配以及量化评级系统；任Union Bank副总裁，负责投资组合风险分析，管理信用组合风险分析以及压力测试模型和流程。\r\n\r\n潘亦婷曾在Aurora投资管理公司任定量分析部门主管，并曾担任ABN AMRO / Lasalle bank资深风险分析师。\r\n\r\n潘亦婷取得美国芝加哥大学Booth商学院的MBA学位、美国伊利诺伊大学芝加哥分校数学与计算机应用硕士学位、南京理工大学计算机科学学士学位与人工智能硕士学位。\r\n','8.png','风控主管',0,NULL,2,3,'美国芝加哥大学Booth商学院',3,'美国伊利诺伊大学芝加哥分校',0,NULL,0,2,2,2,NULL,'2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(5,1,'段念','static/files/busimg/c3702e2cff8c405fb53f74734688d2a8.png','技术主管\r\n\r\n段念先生在加入宜人贷之前，在中国领先的社交网络服务网站豆瓣网任主管技术的副总裁。并曾在中国社交类游戏公司乐元素任技术副总裁。\r\n\r\n段念曾在国内外知名互联网科技及通信类企业担任过技术管理岗位，如谷歌和华为等。\r\n\r\n段念取得中国华中科技大学的工程学士及硕士学位。','9.png','技术主管',1,NULL,2,2,'华中科技大学',0,NULL,0,NULL,0,2,3,2,NULL,'2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(6,1,'种骥科','static/files/busimg/bc21fb4e815645dea5a5bf42532658b5.png','数据主管\r\n\r\n种骥科先生有多年互联网，大数据及金融创新经验。\r\n\r\n在加入宜人贷之前，任职于美国Simply Hired招聘平台，创建了数据科学部， 并应邀为白宫科技办公室参谋大数据技术产品设计。种骥科曾就职于美国Silver Lake 私募公司任Kraftwerk基金数据科学架构师，负责大数据技术应用。种骥科曾任美国卡内基梅隆大学教授与博士生导师，开创了卡内基梅隆大学高性能计算研究教学中心，任联席总监。\r\n\r\n种骥科持有加州大学伯克利分校电子工程和计算机科学系博士学位，卡内基梅隆大学电子和计算机工程系硕士及本科学位，持有8项美国专利(5项获准，3项待批)。','10.png','数据主管',1,NULL,3,3,'加州大学伯克利分校',3,'卡内基梅隆大学',0,NULL,5,3,3,2,'种骥科曾任美国卡内基梅隆大学教授与博士生导师，开创了卡内基梅隆大学高性能计算研究教学中心，任联席总监。持有8项美国专利(5项获准，3项待批)','2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(7,2,'姚宏','static/files/busimg/ef34c3cf63a54caa9f029a16306508cc.png','CEO\r\n\r\n微贷网创始人、CEO，互联网+金融领军人物，浙江P2P平台开拓者。以锐意进取、开拓创新的精神，带领微贷网开创了互联网金融一个又一个的传奇：国内首家专注于汽车抵押借贷业务的互联网金融服务平台，国内首家设立投资人监督机构，浙江省第一家引进亿元战略风投的平台。在姚宏带领下，微贷网几百家营业部遍布全国各地，平台交易额已达几百亿，傲立于互联网金融车贷行业之首，入围福布斯中国互联网金融50强。','5.png','CEO',1,NULL,5,2,'中欧国际工商学院',0,NULL,0,NULL,0,1,2,1,NULL,'2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(8,2,'陈峰','static/files/busimg/82df8a9177e4419090d92a09787d95a5.png','副总裁\r\n\r\n拥有超过16年的国内大型金融企业高级管理层和国际领先会计师事务所的管理咨询及审计经验，对金融业的财务管理、资金管理、投融资管理、信用风险、流动性风险、操作风险、流程优化、系统实施等方面有深入的理解和丰富经验。\r\n','6.png','副总裁',1,NULL,0,0,NULL,0,NULL,1,NULL,4,2,3,2,'中国注册会计师、中国注册税务师 ','2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(9,2,'丁德胜','static/files/busimg/8fc2a24c5c5f4948a34ad90ed6e7f3d1.png','副总裁\r\n\r\n10年以上销售团队的管理经验，在金融行业积累了一定的资源，更练就了一套张弛有度的科学管理方法，能够在较短的时间内打造出一支优秀营销团队。\r\n','7.png','副总裁',1,NULL,1,2,'华东师范大学',0,NULL,1,NULL,3,2,3,1,NULL,'2017-03-18 16:39:53','2017-03-18 16:40:40',0),
	(10,2,'蒋文','static/files/busimg/dd71b0bd2b8e456abbd331d55be43129.png','副总裁\r\n\r\n拥有10年以上的跨国公司和大型公司的IT管理经验，参与公司业务战略规划的制定和执行，主持制定和实施集团或区域IT战略，并以此为基础开展各项相关工作。带领跨国IT团队，培养核心人员，提供集中化的IT服务，对互联网有深刻的理解，有大规模互联网软件开发技术团队管理经验。','8.png','副总裁',1,NULL,3,3,'英国曼彻斯特大学',3,'美国康奈尔大学',0,NULL,3,2,2,1,'“2013年度中国优秀CIO”、“2013年度中国信息化贡献人物”','2017-03-18 16:39:53','2017-03-18 16:40:40',0),
